import { Component, OnInit } from '@angular/core';
import { ReportesService } from 'src/app/equipo-tecnico/reportes/reportes.service';
import { Pedido } from 'src/app/models/pedido';
import swal from 'sweetalert2';
import { AsesorService } from '../asesor.service';

@Component({
  selector: 'SIGCA-pedido',
  templateUrl: './pedido.component.html',
  styleUrls: ['./pedido.component.css']
})
export class PedidoComponent implements OnInit {
fecha: any=[];
ped: Pedido[]=[];
idbanco=Number(sessionStorage.getItem('banco'));
socios:any=[];
  constructor(private pedido:ReportesService, private asesor:AsesorService) { }

  ngOnInit(): void {

    this.pedido.listarSocios(this.idbanco).subscribe(listas=>{
      this.socios=listas;
      console.log( this.socios);

    });
  }
listar(i:number){

  var fecha=this.fecha.se;
  var socio=Number(this.socios[i].ID_PERSONA);

  if(fecha!=null){
    this.asesor.Pedidos(fecha,socio).subscribe(listas=>{
      this.ped=listas;
      console.log( this.ped);

    });
  }else{
    swal.fire({
      title: 'NO SELECCIONO UNA FECHA',
      text: 'Debe seleccionar una fecha para poder ver los pedidos',
      icon: 'warning',
      confirmButtonText: 'OK'
    })
  }

}
}
<div class="container-fluid">
    <div class="row">
        <div class="col-6">
            <div class="card" style="width:100%; margin-top: 60px;">
                <div class="card-body">
                  <h5 class="card-title">Lista de Socios</h5>
                  <div class="row">
                     <div class="col-8" style="text-align: end; padding-top: 3px;">
                      <p>SELECCIONE FECHA:</p>
                     </div>
                     <div class="col-4">
                         <input type="date" width="100%" [(ngModel)]="fecha.se">
                    </div>

                  </div>
                  <div class="card" style="width:100%; margin-bottom: 5px;" *ngFor="let so of socios; let i=index" >
                    <div class="row" >
                        <div class="col-10">
                            <div class="row">
                                <div class="col-2" style="padding-left: 3%; padding-top: 2%;">
                                    <i class="fas fa-user" style="font-size: 80px;"></i>

                                </div>
                                <div class="col-10" style="padding-top: 7%;">

                                    {{so.Persona}}
                                </div>

                            </div>
                        </div>
                        <div class="col-2" style="border-left: 1px solid black; padding-top: 5%; text-align: center;">

                            <button style="border: none; background: none;" (click)="listar(i)">  <i class="fas fa-eye" style="font-size: 100%;"></i></button>
                        </div>
                    </div>
                  </div>



                </div>
              </div>
        </div>
        <div class="col-6">
            <div class="card" style="width:100%; margin-top: 60px;">
                <div class="card-body">
                  <h5 class="card-title">Pedidos de Oracion</h5>
                  <div class="card">
                      <div class="card-body" *ngFor="let pe of ped;let in=index">
                          <h5 class="card-title">{{in+1}}.</h5>
                          <p class="card-text">{{pe.de_pedidooracion}}</p>
                      </div>
                  </div>

                </div>

              </div>

        </div>
    </div>
</div>
