import {
  Component,
  ElementRef,
  EventEmitter,
  OnChanges,
  OnInit,
  Output,
  SimpleChanges,
  ViewChild,
} from '@angular/core';
import { Categoria } from 'src/app/models/categoria';
import { Modulo } from 'src/app/models/modulo';
import { Sesion } from 'src/app/models/sesion';
import swal from 'sweetalert2';
import { ModulosService } from '../service/modulos.service';
@Component({
  selector: 'SIGCA-crear-modulo-principal',
  templateUrl: './crear-modulo-principal.component.html',
  styleUrls: ['./crear-modulo-principal.component.css'],
})
export class CrearModuloPrincipalComponent implements OnInit, OnChanges {
  estadoUpdateDelete: boolean = false;
  selectModulo: any[] = [];
  sesion: Sesion[] = [];
  nombreModulo = '';
  inSesion: Sesion = new Sesion();
  descripcion = '';
  id_modulo: number = 0;
  id_categoria: number = 0;
  insModulo: Modulo = new Modulo();
  updateModulo: Modulo = new Modulo();
  selectCategoria: Categoria[] = [];
  //@Output() datosSesion: EventEmitter<any> = new EventEmitter();
  @Output() idModulo: EventEmitter<number> = new EventEmitter();
  @ViewChild('modulo') modulo: ElementRef | any;
  @ViewChild('categoria') categoria: ElementRef | any;
  @ViewChild('categoriaupdate') categoria2: ElementRef | any;

  constructor(private moduloservice: ModulosService) {}
  ngOnChanges(): void {
    this.traerSesion(this.id_modulo);
  }

  ngOnInit(): void {
    this.cargarModulos();
  }

  // TODO: ESTE METODO TRAE LOS MODULO PARA PODER MOSTRARLO EN EL COMBO DE LA IZQUIERDA
  cargarModulos(): void {
    this.EliminarModulos();
    this.moduloservice.getModulo().subscribe((data) => {
      data.find((e) => {
        this.selectModulo.push(e);
      });
    });
    // console.log(this.selectModulo.find((e) => console.log(e.id_modulo)));
  }

  EliminarModulos(): void {
    this.moduloservice.getModulo().subscribe((data) => {
      data.find((e) => {
        this.selectModulo.pop();
      });
    });
    // console.log(this.selectModulo.find((e) => console.log(e.id_modulo)));
  }

  abrirModal(): void {
    this.cargarCategoria();
  }

  // TODO: ESTE METODO TRAE LA CATEGORIA PARA PODER MOSTRARLO EN EL COMBO DEL MODAL
  cargarCategoria(): void {
    this.moduloservice.getCategoria().subscribe((data) => {
      console.log(data);
      this.selectCategoria = data;
    });
  }

  //TODO: OBTIENE EL ID DEL MODULO AL DAR CLICK EN EL MODAL EN SI y trae las sesiones de lo seccionado
  getVal(): void {
    const select = this.modulo.nativeElement;
    const selectdOption = select.options[select.selectedIndex].value;
    this.traerSesion(selectdOption);
    this.id_modulo = selectdOption;
    this.estadoUpdateDelete = true;
  }

  getvalCategoria(): number {
    const select = this.categoria.nativeElement;
    const selectdOption = select.options[select.selectedIndex].value;
    console.log(selectdOption);
    return selectdOption;
  }

  getvalCategoriaUpdate(): number {
    const select = this.categoria2.nativeElement;
    const selectdOption = select.options[select.selectedIndex].value;
    console.log(selectdOption);
    return selectdOption;
  }

  //TODO: GUARDA LOS DATOS DEL MODAL
  guardar(): void {
    swal
      .fire({
        title: "<h1 style='color:blue'>Agregar</h1>",
        text: '¿Desea Agregar este Modulo ?',
        icon: 'question',
        showCancelButton: true,
        confirmButtonText: 'Confirmar',
        cancelButtonText: 'Cancelar',
        cancelButtonColor: '#DD6B55',
        footer: '<span class="red">Lea antes de confirmar</span>',
        allowOutsideClick: false,
      })
      .then((resultado) => {
        if (resultado.value) {
          this.insModulo.no_modulo = this.nombreModulo;
          console.log(this.getvalCategoria());
          this.insModulo.categoria = { id_categoria: this.getvalCategoria() };
          this.moduloservice.insertarModulo(this.insModulo).subscribe((e) => {
            console.log(e);
          });
          swal.fire({
            position: 'center',
            icon: 'success',
            title: 'Tu Modulo se Guardo con exito',
            showConfirmButton: false,
            timer: 1500,
          });
          this.limpiar();
          this.cargarModulos();
        } else {
          // Dijeron que no
          this.limpiar();
        }
      });
  }

  //TODO: LIMPIA LOS CAMPOS DEL MODAL AL SALIR Y GUARDAR
  limpiar(): void {
    this.nombreModulo = '';
    this.insModulo.no_modulo = '';
    this.updateModulo.no_modulo = '';
    const select = this.categoria.nativeElement;
    const selectdOption = select.options[select.selectedIndex];
    selectdOption.value = null;
  }

  deleteModulo() {
    swal
      .fire({
        title: "<h1 style='color:red'>Eliminar</h1>",
        text: '¿Desea eliminar el módulo?',
        icon: 'warning',
        showCancelButton: true,
        confirmButtonText: 'Confirmar',
        cancelButtonText: 'Cancelar',
        cancelButtonColor: '#DD6B55',
        footer: '<span class="red">Lea antes de confirmar</span>',
        allowOutsideClick: false,
      })
      .then((resultado) => {
        if (resultado.value) {
          // Hicieron click en "Sí"
          this.moduloservice.deleteModulo(this.id_modulo).subscribe((data) => {
            console.log(data);
          });
          this.cargarModulos();
        } else {
          // Dijeron que no
          console.log('*NO se elimina la venta*');
        }
      });
  }

  updateModuloLleanarDatos() {
    this.cargarCategoria();
    console.log(this.id_modulo);
    this.moduloservice.getModuloId(this.id_modulo).subscribe((e) => {
      console.log(e);
      this.updateModulo = e;
    });
  }

  editarModulo() {
    swal
      .fire({
        title: "<h1 style='color:blue'>Editar</h1>",
        text: '¿Desea editar el módulo?',
        icon: 'question',
        showCancelButton: true,
        confirmButtonText: 'Confirmar',
        cancelButtonText: 'Cancelar',
        cancelButtonColor: '#DD6B55',
        footer: '<span class="red">Lea antes de confirmar</span>',
        allowOutsideClick: false,
      })
      .then((resultado) => {
        if (resultado.value) {
          // Hicieron click en "Sí"
          this.updateModulo.no_modulo = this.nombreModulo;
          this.updateModulo.categoria = {
            id_categoria: this.getvalCategoriaUpdate(),
          };
          this.moduloservice
            .updateModulo(this.id_modulo, this.updateModulo)
            .subscribe((e) => console.log(e));
          swal.fire({
            position: 'center',
            icon: 'success',
            title: 'Tu Modulo se actualizo con exito',
            showConfirmButton: false,
            timer: 1500,
          });
          this.limpiar();
          this.cargarModulos();
        } else {
          // Dijeron que no
          console.log('*NO se elimina la venta*');
        }
      });
  }

  // TODO: SESION

  traerSesion(id: number): void {
    this.idModulo.emit(id);
  }

  insertarSesion(): void {
    //alert(this.inSesion.no_sesion);
    swal
      .fire({
        title: "<h1 style='color:blue'>Agregar</h1>",
        text: '¿Desea Agregar la Sesion?',
        icon: 'question',
        showCancelButton: true,
        confirmButtonText: 'Confirmar',
        cancelButtonText: 'Cancelar',
        cancelButtonColor: '#DD6B55',
        footer: '<span class="red">Lea antes de confirmar</span>',
        allowOutsideClick: false,
      })
      .then((resultado) => {
        if (resultado.value) {
          this.inSesion.modulo = { id_modulo: this.id_modulo };
          this.moduloservice
            .insertarSesion(this.inSesion)
            .subscribe((e) => console.log(e));
          swal.fire({
            position: 'center',
            icon: 'success',
            title: 'Tu Sesion se Guardo con exito',
            showConfirmButton: false,
            timer: 1500,
          });
          this.traerSesion(this.id_modulo);
          this.limpiarSesion();
        } else {
          // Dijeron que no
          this.limpiarSesion();
        }
      });
  }

  limpiarSesion(): void {
    this.inSesion.no_sesion = '';
    this.inSesion.id_sesion = 0;
    this.inSesion.modulo = null;
  }
}
<article class="p-2">
  <Header class="w-100 mt-3 d-flex justify-content-center">
    <h1 class="m-0">MÓDULOS</h1>
  </Header>
  <hr />
  <div class="container-fluid d-flex justify-content-center w-100 mt-4">
    <div class="d-flex align-items-center">
      <select
        #modulo
        class="form-select"
        name="modulos"
        aria-label="Default select example"
        (change)="getVal()"
      >
        <option selected autofocus>Selecione Módulo</option>
        <option
          value="{{ modulo.id_modulo }}"
          class="opcionesModulos"
          *ngFor="let modulo of selectModulo"
          (click)="traerSesion(modulo.id_modulo)"
        >
          {{ modulo.no_modulo }}
        </option>
      </select>
      <div
        class="d-flex align-items-center w-100 position-relative"
        *ngIf="estadoUpdateDelete"
      >
        <i
          class="
            fas
            fa-plus-square
            size-icon size-icon--movesesion
            text-warning
          "
          data-bs-toggle="modal"
          data-bs-target="#modalSesion"
        ></i>
        <i
          (click)="deleteModulo()"
          class="far fa-trash-alt text-danger size-icon size-icon--movedelete"
        ></i>
        <i
          data-bs-toggle="modal"
          data-bs-target="#modalUpdate"
          (click)="updateModuloLleanarDatos()"
          class="fas fa-edit text-success size-icon size-icon--moveupdate"
        ></i>
      </div>
    </div>
    <button
      (click)="abrirModal()"
      type="button"
      class="btn btn-outline-success"
      data-bs-toggle="modal"
      data-bs-target="#miModal"
    >
      +
    </button>
    <select class="form-select" aria-label="Default select example">
      <option disabled autofocus>Selecione Año</option>
      <option value="1">2021</option>
      <option value="2">2020</option>
      <option value="3">2019</option>
    </select>
  </div>

  <!-- ** Modal para el guardar  -->

  <div
    class="modal fade"
    id="miModal"
    tabindex="-1"
    aria-labelledby="modalTitle"
    aria-hidden="true"
  >
    <div class="modal-dialog">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title h3">Registrar Capacitacion</h5>
          <button
            type="button"
            class="btn-close"
            data-bs-dismiss="modal"
            aria-label="Close"
          ></button>
        </div>
        <div class="modal-body modal-size">
          <form>
            <div class="mb-3">
              <input
                name="nombreModulo"
                type="text"
                class="form-control nuevoModulo"
                [(ngModel)]="nombreModulo"
                placeholder=" Ingrese Modulo"
              />
            </div>
            <div class="mb-3">
              <select
                #categoria
                class="w-100 form-select categoria"
                aria-label="Default select example"
                (change)="getvalCategoria()"
              >
                <option selected disabled autofocus>
                  Seleccione Categoria
                </option>
                <option
                  value="{{ categoria.id_categoria }}"
                  *ngFor="let categoria of selectCategoria"
                >
                  {{ categoria.no_categoria }}
                </option>
              </select>
            </div>
          </form>
        </div>
        <div class="modal-footer">
          <button
            type="button"
            class="btn btn-secondary"
            data-bs-dismiss="modal"
            (click)="limpiar()"
          >
            Cerrar
          </button>
          <button
            type="button"
            class="btn btn-primary guardarModulo"
            data-bs-dismiss="modal"
            (click)="guardar()"
          >
            Guardar
          </button>
        </div>
      </div>
    </div>
  </div>

  <!-- ** Modal para el EDITAR  -->

  <div
    class="modal fade"
    id="modalUpdate"
    tabindex="-1"
    aria-labelledby="modalTitle"
    aria-hidden="true"
  >
    <div class="modal-dialog">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title h3">Actualizar Capacitacion</h5>
          <button
            type="button"
            class="btn-close"
            data-bs-dismiss="modal"
            aria-label="Close"
          ></button>
        </div>
        <div class="modal-body modal-size">
          <form>
            <div class="mb-3">
              <input
                name="nombreModulo"
                type="text"
                class="form-control actualizarCapacitacion"
                [value]="updateModulo.no_modulo"
                [(ngModel)]="nombreModulo"
                placeholder=" Ingrese Modulo"
              />
            </div>
            <div class="mb-3">
              <select
                #categoriaupdate
                class="w-100 form-select actualizarCapacitacion1"
                aria-label="Default select example"
                (change)="getvalCategoriaUpdate()"
              >
                <option selected disabled autofocus>
                  Seleccione Categoria
                </option>
                <option
                  value="{{ categoria.id_categoria }}"
                  *ngFor="let categoria of selectCategoria"
                >
                  {{ categoria.no_categoria }}
                </option>
              </select>
            </div>
          </form>
        </div>
        <div class="modal-footer">
          <button
            type="button"
            class="btn btn-secondary"
            data-bs-dismiss="modal"
            (click)="limpiar()"
          >
            Cerrar
          </button>
          <button
            type="button"
            class="btn btn-primary actualizarCapacitacion2 "
            data-bs-dismiss="modal"
            (click)="editarModulo()"
          >
            Guardar
          </button>
        </div>
      </div>
    </div>
  </div>

  <!-- ** Modal para el Sesion -->

  <div
    class="modal fade"
    id="modalSesion"
    tabindex="-1"
    aria-labelledby="modalTitle"
    aria-hidden="true"
  >
    <div class="modal-dialog">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title h3">Agregar Sesion</h5>
          <button
            type="button"
            class="btn-close"
            data-bs-dismiss="modal"
            aria-label="Close"
          ></button>
        </div>
        <div class="modal-body modal-size-sesion">
          <form>
            <div class="mb-3">
              <input
                name="nombreModulo"
                type="text"
                class="form-control sesionNueva"
                [(ngModel)]="inSesion.no_sesion"
                placeholder=" Ingrese Sesion"
              />
            </div>
          </form>
        </div>
        <div class="modal-footer">
          <button
            type="button"
            class="btn btn-secondary"
            data-bs-dismiss="modal"
            (click)="limpiarSesion()"
          >
            Cerrar
          </button>
          <button
            type="button"
            class="btn btn-primary guardarSesion"
            data-bs-dismiss="modal"
            (click)="insertarSesion()"
          >
            Guardar
          </button>
        </div>
      </div>
    </div>
  </div>
</article>
