import { Component, OnInit, Output, EventEmitter } from '@angular/core';
import { Progreso } from 'src/app/models/Progreso';
import { SocioService } from '../socio.service';

@Component({
  selector: 'SIGCA-ingresarmodulo',
  templateUrl: './ingresarmodulo.component.html',
  styleUrls: ['./ingresarmodulo.component.css']
})
export class IngresarmoduloComponent implements OnInit {
  @Output() link:EventEmitter<string> = new EventEmitter;
  constructor( private profreso:SocioService) { }
  x=Number(sessionStorage.getItem('idusuario'));
  progres: Progreso[]=[];
  nombre="";
  progreso='';
  id=0;
  ngOnInit(): void {
    this.Progreso();
  }

  entrarModulo():void{
    this.link.emit('socio/ingresarmodulo/sesiones')

  }

  Progreso():void{
    this.profreso.getProgreso(this.x).subscribe(listas=>{
      this.progres=listas;
      console.log(this.progres)
      this.nombre=String(this.progres[0].NO_MODULO);
      this.progreso=String(this.progres[0].Avance_Modulo);
      this.id=Number(this.progres[0].FK_MODULO);
    });

  }

  guardar():void{
    var y = String(this.id);
    sessionStorage.setItem('id_modulo', y);
  }
}
<div class="container text-center margin" style="width: 90%;" >
    <div class="col-12 caja" >
      <div class="pedido altur" >
        <div class="divisor"></div>
        <h1 class="medio">
          Mi Módulo</h1>
      </div>
      <div class="caja2">
        <h2 class="caja3"><b>{{nombre}}</b></h2>
      <img
        src="/assets/images/1.jpg"
        class="img-fluid"
        alt=""
      />
      <div class="row">
        <div class="col-10">
          <progress class="barra" max="100"  value={{progreso}}> </progress>
        </div>
        <div class="col-2" style="text-align: center; margin-top: 10px;">Mi Progreso</div>
      </div>
      <div class="caja3">
        <button routerLink="/vistaModulo" type="button" class="btn btn-success boton" (click)="guardar()">Ingresar</button>
      </div>
      <div class="divi">

      </div>
      </div>
    </div>
</div>
