import {
  Component,
  ElementRef,
  EventEmitter,
  Input,
  OnInit,
  Output,
  ViewChild,
} from '@angular/core';
import swal from 'sweetalert2';
import { Sesion } from 'src/app/models/sesion';
import { ModulosService } from '../service/modulos.service';
import { Recurso } from 'src/app/models/recurso';

@Component({
  selector: 'SIGCA-recursos',
  templateUrl: './recursos.component.html',
  styleUrls: ['./recursos.component.css'],
})
export class RecursosComponent implements OnInit {
  @Output() status: EventEmitter<boolean> = new EventEmitter();
  @Input() id_sesion: number = 0;
  @ViewChild('secuencia') secuencia: ElementRef | any;
  sesion: Sesion = new Sesion();
  secuenciaRecurso: number = 0;
  recurso: any[] = [];
  insrecurso: Recurso = new Recurso();
  tipo: any[] = [];
  statusRecurso: boolean = true;
  id_tipo: number = 0;
  constructor(private service: ModulosService) {}

  ngOnInit(): void {
    this.obtenerSesion();
    this.obtenerRecursoSesion();
  }

  cambiarEstado() {
    this.status.emit(true);
  }

  obtenerSesion() {
    this.service.buscarSesionId(this.id_sesion).subscribe((e) => {
      this.sesion.no_sesion = e.no_sesion;
      console.log(this.sesion);
    });
  }

  obtenerRecursoSesion() {
    this.service.buscarRecursoId(this.id_sesion).subscribe((e: any) => {
      this.recurso = e;
      console.log(this.recurso);
    });

    this.secuenciaRecurso = this.recurso.length;
    console.log(this.secuencia);
  }

  // ?? ELIMINAR RECURSO

  eliminarRecurso(id: number) {
    console.log(id);
    swal
      .fire({
        title: "<h1 style='color:red'>Eliminar</h1>",
        text: '¿Desea eliminar este Recurso?',
        icon: 'warning',
        showCancelButton: true,
        confirmButtonText: 'Confirmar',
        cancelButtonText: 'Cancelar',
        cancelButtonColor: '#DD6B55',
        footer: '<span class="red">Lea antes de confirmar</span>',
        allowOutsideClick: false,
      })
      .then((resultado) => {
        if (resultado.value) {
          // Hicieron click en "Sí"
          this.service.eliminarRecurso(id).subscribe();
          swal.fire({
            position: 'center',
            icon: 'success',
            title: 'Tu sesion se elimino con exito',
            showConfirmButton: false,
            timer: 1500,
          });
          this.obtenerRecursoSesion();
        } else {
          // Dijeron que no
          swal.fire({
            position: 'center',
            icon: 'error',
            title: 'Se cancelo la operacion',
            showConfirmButton: false,
            timer: 1000,
          });
        }
      });
  }

  // ?? EDITAR RECURSO

  editarRecurso(id: number) {
    this.service.buscarRecurso(id).subscribe((e) => {
      console.log(e);
      this.insrecurso = e;
    });
    this.traerTipoRecursos();
    this.statusRecurso = false;
  }

  // ?? TRAER TIPO DE RECURSOS

  traerTipoRecursos() {
    this.service.getTipoRecursos().subscribe((e) => {
      this.tipo = e;
      console.log(this.tipo);
    });
  }

  traerObtenerIdTipo(id: number) {
    this.id_tipo = id;
  }

  //?? AGREGAR RECURSO

  agregarRecurso() {
    swal
      .fire({
        title: "<h1 style='color:blue'>Agregar</h1>",
        text: '¿Desea Agregar este Modulo ?',
        icon: 'question',
        showCancelButton: true,
        confirmButtonText: 'Confirmar',
        cancelButtonText: 'Cancelar',
        cancelButtonColor: '#DD6B55',
        footer: '<span class="red">Lea antes de confirmar</span>',
        allowOutsideClick: false,
      })
      .then((resultado) => {
        if (resultado.value) {
          this.insrecurso.orden = this.getvalSecuencia();
          this.insrecurso.sesion = {
            id_sesion: this.id_sesion,
          };
          this.insrecurso.tipo = {
            id_tipo: this.id_tipo,
          };

          console.log(this.insrecurso);
          this.service.insertarRecurso(this.insrecurso).subscribe((e) => {
            console.log(e);
          });
          this.obtenerRecursoSesion();
          this.limpiarDocumento();
          swal.fire({
            position: 'center',
            icon: 'success',
            title: 'Tu Modulo se Guardo con exito',
            showConfirmButton: false,
            timer: 1500,
          });
        } else {
          // Dijeron que no
          swal.fire({
            position: 'center',
            icon: 'error',
            title: 'Se cancelo la operacion',
            showConfirmButton: false,
            timer: 1000,
          });
          this.limpiarDocumento();
        }
      });
    // this.limpiarDocumento();
  }

  actualizarDocumento() {
    swal
      .fire({
        title: "<h1 style='color:blue'>Actualizar</h1>",
        text: '¿Desea Actualizar este Modulo ?',
        icon: 'question',
        showCancelButton: true,
        confirmButtonText: 'Confirmar',
        cancelButtonText: 'Cancelar',
        cancelButtonColor: '#DD6B55',
        footer: '<span class="red">Lea antes de confirmar</span>',
        allowOutsideClick: false,
      })
      .then((resultado) => {
        if (resultado.value) {
          this.insrecurso.orden = this.getvalSecuencia();
          this.insrecurso.sesion = {
            id_sesion: this.id_sesion,
          };
          this.insrecurso.tipo = {
            id_tipo: this.id_tipo,
          };
          console.log(this.insrecurso);
          this.service
            .updateRecurso(this.insrecurso.id, this.insrecurso)
            .subscribe();
          this.obtenerRecursoSesion();
          this.limpiarDocumento();
          swal.fire({
            position: 'center',
            icon: 'success',
            title: 'Tu Modulo se actualizo con exito',
            showConfirmButton: false,
            timer: 1500,
          });
        } else {
          // Dijeron que no
          swal.fire({
            position: 'center',
            icon: 'error',
            title: 'Se cancelo la operacion',
            showConfirmButton: false,
            timer: 1000,
          });
          this.limpiarDocumento();
        }
      });
  }

  //?? OBTENER SECUENCIA

  getvalSecuencia(): number {
    const select = this.secuencia.nativeElement;
    const selectdOption = select.options[select.selectedIndex].value;
    console.log(selectdOption);
    return selectdOption;
  }

  limpiarDocumento() {
    this.insrecurso.nombrerecurso = '';
    this.insrecurso.descripcion = '';
    this.insrecurso.orden = 0;
    this.insrecurso.sesion = null;
    this.insrecurso.tipo = null;
  }
}
<section class="w-100 h-100 p-2 main">
  <div>
    <button class="btn btn-primary" (click)="cambiarEstado()">Regresar</button>
  </div>

  <article class="main-contenedor w-50">
    <header class="main-contenedor__header">
      <h1 class="title d-inline-block w-100 text-white">
        {{ sesion.no_sesion }}
      </h1>
      <div class="main-icon">
        <i
          class="fas fa-plus-square icon icon-agregar"
          data-bs-toggle="modal"
          data-bs-target="#agregarRecurso"
          (click)="traerTipoRecursos()"
        ></i>
      </div>
    </header>

    <section class="main-recursos">
      <article
        class="main-recursos__item"
        *ngFor="let dato of recurso; index as i"
      >
        <p class="d-inline-block name-recurso mb-0">
          {{ i + 1 }}. {{ dato.nombrerecurso }}
        </p>
        <div class="main-icon w-25">
          <i
            class="far fa-trash-alt icon icon--red"
            (click)="eliminarRecurso(dato.id)"
          ></i>
          <i
            class="fas fa-edit icon icon--blue"
            data-bs-toggle="modal"
            data-bs-target="#agregarRecurso"
            (click)="editarRecurso(dato.id)"
          ></i>
        </div>
      </article>
    </section>
  </article>
</section>

<!-- TODO: MODAL PARA SELECCIONAR RECURSO -->

<div
  class="modal fade"
  id="agregarRecurso"
  tabindex="-1"
  aria-labelledby="modalTitle"
  aria-hidden="true"
>
  <div class="modal-dialog modal-xl">
    <div class="modal-content bg-modal">
      <div class="modal-header">
        <h5 class="modal-title d-block h3 text-white text-center title-modal">
          Elegir Recurso
        </h5>
        <button
          type="button"
          class="btn-close btn-danger"
          data-bs-dismiss="modal"
          aria-label="Close"
        ></button>
      </div>
      <div class="modal-body modal-size-recurso">
        <form>
          <div class="mb-3 d-flex justify-content-center">
            <div
              *ngFor="let tipo of tipo"
              class="d-flex justify-content-center"
            >
              <button
                *ngIf="tipo.id_tipo == 1"
                class="button btn"
                data-bs-dismiss="modal"
                data-bs-toggle="modal"
                data-bs-target="#agregarDocumento"
                (click)="traerObtenerIdTipo(tipo.id_tipo)"
              >
                <img src="/assets/images/documento.png" alt="" />
              </button>
              <button
                *ngIf="tipo.id_tipo == 3"
                class="button btn"
                data-bs-dismiss="modal"
                data-bs-toggle="modal"
                data-bs-target="#agregarCuestionario"
                (click)="traerObtenerIdTipo(tipo.id_tipo)"
              >
                <img src="/assets/images/cuestionario.png" alt="" />
              </button>
              <button
                *ngIf="tipo.id_tipo == 2"
                class="button btn"
                data-bs-dismiss="modal"
                data-bs-toggle="modal"
                data-bs-target="#agregarVideo"
                (click)="traerObtenerIdTipo(tipo.id_tipo)"
              >
                <img src="/assets/images/video.png" alt="" />
              </button>
            </div>
          </div>
        </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">
          Cerrar
        </button>
      </div>
    </div>
  </div>
</div>

<!-- ** MODAL PARA EL DOCUMENTO -->

<div
  class="modal fade"
  id="agregarDocumento"
  tabindex="-1"
  aria-labelledby="modalTitle"
  aria-hidden="true"
>
  <div class="modal-dialog">
    <div class="modal-content bg-modal">
      <div class="modal-header" *ngIf="statusRecurso">
        <h5 class="modal-title h3 text-white">Registrar Documento</h5>
        <button
          type="button"
          class="btn-close text-danger"
          data-bs-dismiss="modal"
          aria-label="Close"
        ></button>
      </div>
      <div class="modal-header" *ngIf="!statusRecurso">
        <h5 class="modal-title h3 text-white">Actualizar Documento</h5>
        <button
          type="button"
          class="btn-close text-danger"
          data-bs-dismiss="modal"
          aria-label="Close"
        ></button>
      </div>
      <div
        class="
          p-4
          modal-body modal-size
          d-flex
          flex-column
          align-items-center
          justify-content-center
        "
      >
        <form>
          <div class="mb-3">
            <input
              name="nombreModulo"
              type="text"
              class="form-control"
              placeholder=" Ingrese Nombre"
              [(ngModel)]="insrecurso.nombrerecurso"
            />
          </div>
          <div class="mb-3">
            <input
              name="nombreModulo"
              type="file"
              class="form-control mb-3"
              placeholder=" Ingrese Archivo"
            />
          </div>
          <div class="mb-3">
            <textarea
              class="form-control mb-3"
              type="text"
              name="descripcion"
              id="desc"
              cols="30"
              rows="6"
              [(ngModel)]="insrecurso.descripcion"
              placeholder="Ingresar Descripcion"
            ></textarea>
          </div>
          <div class="mb-3">
            <select
              #secuencia
              class="w-100 form-select"
              aria-label="Default select example"
            >
              <option selected disabled autofocus>Seleccione Secuencia</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
            </select>
          </div>
        </form>
      </div>
      <div class="modal-footer" *ngIf="statusRecurso">
        <button
          type="button"
          class="btn btn-danger"
          data-bs-dismiss="modal"
          (click)="limpiarDocumento()"
        >
          Cerrar
        </button>
        <button
          type="button"
          class="btn btn-primary"
          data-bs-dismiss="modal"
          (click)="agregarRecurso()"
        >
          Guardar
        </button>
      </div>
      <div class="modal-footer" *ngIf="!statusRecurso">
        <button
          type="button"
          class="btn btn-danger"
          data-bs-dismiss="modal"
          (click)="limpiarDocumento()"
        >
          Cerrar
        </button>
        <button
          type="button"
          class="btn btn-primary"
          data-bs-dismiss="modal"
          (click)="actualizarDocumento()"
        >
          Actualizar
        </button>
      </div>
    </div>
  </div>
</div>

<!-- ** MODAL PARA EL CUESTIONARIO -->

<!-- ** MODAL PARA EL VIDEO -->
