import {
  Component,
  ElementRef,
  OnInit,
  Renderer2,
  ViewChild,
} from '@angular/core';

@Component({
  selector: 'SIGCA-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css'],
})
export class PrincipalComponent implements OnInit {
  statusReceived: boolean = true;
  UrlIframe: string = '';
  @ViewChild('marcoIframe') iframe: ElementRef | any;
  @ViewChild('sidebar') sidebar: ElementRef | any;

  constructor(private ren2: Renderer2) {}

  ngOnInit(): void {}

  recibirEstado(status: any) {
    this.statusReceived = status;
  }



  recibirLink(link:string):void{
    console.log(link)
    const direccion = this.iframe.nativeElement;
    this.ren2.setAttribute(direccion, 'src', link);
  }
}
<div class="container-fluid margenes-costado">
  <header class="row m-0 size-1">
    <div class="col-xl-12 p-0">
      <SIGCA-nav-top (statusSidebar)="recibirEstado($event)"></SIGCA-nav-top>
    </div>
  </header>

  <!-- <button #cambio (click)="valor()">Cambiar</button> -->

  <section class="row m-0 size-2">
    <div #sidebar class="col-xl-2 p-0 animation">
      <SIGCA-sidebar
        (link)="recibirLink($event)"
        class="m-0"
        [opened]="statusReceived"
      ></SIGCA-sidebar>
    </div>
    <div

      class="col-xl-10 main-info"
    >
    <router-outlet></router-outlet>
  </div>
  </section>
</div>
